function loginRequest(){const e=$("#login").val(),a=$("#password").val();$.ajax({url:"/auth",type:"GET",data:{login:e,password:a},dataType:"html",success:function(e){try{$(".error_message").empty(),$(".error_message").fadeIn(200).append(JSON.parse(e).message)}catch{$(".error_message").remove(),location.reload()}}})}$(document).keypress(function(e){13==e.which&&loginRequest()}),$("#auth").click(function(){loginRequest()});