$(document).keypress(function(a){13==a.which&&loginRequest()}),$("#auth").click(function(){loginRequest()});function loginRequest(){const a=$("#login").val(),b=$("#password").val();$.ajax({url:"/auth",type:"GET",data:{login:a,password:b},dataType:"html",success:function(a){try{$(".error_message").empty(),$(".error_message").fadeIn(200).append(JSON.parse(a).message)}catch{$(".error_message").remove(),location.reload()}}})}